from langchain_community.document_loaders import PyMuPDFLoader  # For PDF
from langchain_community.vectorstores import FAISS

from langchain.vectorstores import FAISS
from langchain.document_loaders import TextLoader
from langchain.text_splitter import CharacterTextSplitter
from langchain_google_genai import GoogleGenerativeAIEmbeddings
from langchain_google_genai import ChatGoogleGenerativeAI

def build_vector_store_from_docs(doc_paths):
    all_docs = []

    for path in doc_paths:
        if path.endswith(".pdf"):
            loader = PyMuPDFLoader(path)  # this handles PDF
        else:
            from langchain_community.document_loaders import TextLoader
            loader = TextLoader(path, encoding='utf-8')

        docs = loader.load()
        all_docs.extend(docs)

    embeddings = GoogleGenerativeAIEmbeddings(model="models/embedding-001")
    vector_store = FAISS.from_documents(all_docs, embeddings)
    return vector_store

"""**Analyst Agent  RAG with Gemini**"""

from langchain.chains import RetrievalQA

def create_analyst_agent(vector_store):
    llm = ChatGoogleGenerativeAI(model="gemini-pro", temperature=0.2)
    qa_chain = RetrievalQA.from_chain_type(
        llm=llm,
        retriever=vector_store.as_retriever(),
        return_source_documents=False
    )
    return qa_chain

"""**Valuation Agent  DCF Stub (Template-based)**"""

def dcf_valuation(eps, growth_rate=0.10, discount_rate=0.08, years=5):
    cash_flows = [eps * (1 + growth_rate) ** i for i in range(1, years + 1)]
    discounted = [cf / ((1 + discount_rate) ** i) for i, cf in enumerate(cash_flows, 1)]
    terminal_value = (cash_flows[-1] * (1 + growth_rate)) / (discount_rate - growth_rate)
    terminal_discounted = terminal_value / ((1 + discount_rate) ** years)
    return sum(discounted) + terminal_discounted

"""**Equity Research Report Generator Agent**"""

def generate_equity_report(ticker, vector_store):
    stock_data = fetch_stock_data(ticker)
    news = fetch_financial_news(stock_data['Company'])
    analyst = create_analyst_agent(vector_store)

    # Summary from RAG
    financial_summary = analyst.run(f"Summarize financial performance and risks of {stock_data['Company']}")

    # DCF
    fair_value = dcf_valuation(stock_data['EPS'] or 5)

    # Final Report
    report = f"""
    EQUITY RESEARCH REPORT: {stock_data['Company']}
    Sector: {stock_data['Sector']}

    Summary:
    {financial_summary}

     News Highlights:
    {'; '.join(news)}

    Valuation:
    Current Price: ${stock_data['Current Price']}
    Estimated Fair Value (DCF): ${round(fair_value, 2)}

    Market Metrics:
    PE Ratio: {stock_data['PE Ratio']}
    52-Week Range: ${stock_data['52 Week Low']} - ${stock_data['52 Week High']}
    Beta: {stock_data['Beta']}

    Investment Insight:
    Based on our analysis, {stock_data['Company']} appears to be {'undervalued' if fair_value > stock_data['Current Price'] else 'overvalued'}.
    """

    return report

"""**End-to-End Agent**"""

