import matplotlib.pyplot as plt

"""***Price Trend Chart***"""

def plot_price_trend(ticker):
    data = yf.download(ticker, period="6mo", interval="1d")
    data['Close'].plot(figsize=(10, 4), title=f"{ticker} Price Trend (Last 6 Months)")
    plt.ylabel("Price ($)")
    plt.grid()
    plt.show()

plot_price_trend("AAPL")

## Advanced Valuation  Add Comparables with DCF

def comparables_valuation(pe_ratio, eps):
    if not pe_ratio or not eps:
        return None
    return pe_ratio * eps  # Simplified comparables method (P/E * EPS)

"""**Add PDF Support to FAISS Vector Store**"""

def load_pdf_into_vector_store(pdf_paths):
    docs = []
    text_splitter = CharacterTextSplitter(chunk_size=1000, chunk_overlap=100)
    embedding = GoogleGenerativeAIEmbeddings(model="models/embedding-001")

    for path in pdf_paths:
        loader = PyMuPDFLoader(path)
        raw_docs = loader.load()
        docs.extend(text_splitter.split_documents(raw_docs))

    return FAISS.from_documents(docs, embedding)

pdf_paths = ["/content/company_10k.pdf"]
vector_store = load_pdf_into_vector_store(pdf_paths)

"""**Export Final Report as PDF**"""

from fpdf import FPDF

def save_report_as_pdf(report_text, filename="Equity_Research_Report.pdf"):
    pdf = FPDF()
    pdf.add_page()
    pdf.set_auto_page_break(auto=True, margin=15)
    pdf.set_font("Arial", size=12)
    for line in report_text.split("\n"):
        pdf.multi_cell(0, 10, line)
    pdf.output(filename)
    print(f"Report saved as {filename}")

report = generate_equity_report("AAPL", vector_store)
save_report_as_pdf(report)